import React, {useState, useEffect} from "react";
import {Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import '../styles/Home.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import '../styles/Cards.css'

export default function Cards() {
    const [rows, setRows] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const location = useLocation();
    const navigate = useNavigate();
    const [secondsLeft, setSecondsLeft] = useState(10);
    const [isDisabled, setIsDisabled] = useState(true);


        const deleteRow = async (id) => {

            const handleDelete = () => {
                axios.delete(`http://localhost:8080/content/delete/${id}`).then(() => {
                    loadRows();
                });
            };

            const buttons = [
                {
                    label: 'Отмена',
                    onClick: () => {},
                },
                {
                    label: `Удалить ${secondsLeft}`,
                    onClick: handleDelete,
                    disabled: isDisabled,
                    className: isDisabled ? 'btn-secondary' : 'btn-danger',
                },
            ];

            const confirmOptions = {
                title: 'Подтвердите удаление',
                message: 'Вы действительно хотите удалить эту публикацию?',
                buttons,
            };

            const dialog = confirmAlert(confirmOptions);

            const updateSecondsLeft = () => {
                setSecondsLeft((secondsLeft) => secondsLeft - 1);
            }

            const intervalId = setInterval(updateSecondsLeft, 1000);

            dialog.customUI = ({ onClose }) => {
                return (
                    <div className="custom-ui">
                        <div className="custom-ui-message">{confirmOptions.message}</div>
                        <div className="custom-ui-buttons">
                            {buttons.map((button, index) => (
                                <button
                                    key={index}
                                    className={`btn ${button.className}`}
                                    onClick={() => {
                                        button.onClick();
                                        onClose();
                                    }}
                                    disabled={button.disabled}
                                >
                                    {button.label}
                                </button>
                            ))}
                        </div>
                    </div>
                );
            };

            dialog.customUI.onClose = () => clearInterval(intervalId);
        };

    useEffect(() => {
        loadRows();
    }, []);

    useEffect(() => {
        const timer = setTimeout(() => {
            if (secondsLeft > 0) {
                setSecondsLeft(secondsLeft - 1);
            } else {
                setIsDisabled(false);
            }
        }, 1000);

        return () => {
            clearTimeout(timer);
        };
    }, [secondsLeft]);

    const loadRows = async () => {
        const result = await axios.get("http://localhost:8080/content");
        setRows(result.data);
    };

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const searchQuery = queryParams.get("search");
        setSearchTerm(searchQuery || "");
    }, [location]);

    const filteredRows = rows.filter(
        (row) =>
            row.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            row.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
            row.maintext.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="container">
            <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4 g-4 mt-4">
                {filteredRows.map((row, index) => (
                    <div className="col" key={index}>
                        <div className="card-wrapper">
                            <div className="card border shadow hover-zoom card-link custom-height" onClick={() => navigate(`/content/view/${row.id}`)}>
                                <div className="card-body">
                                    <h5 className="card-title">{row.title}</h5>
                                    <p className="card-text"><strong></strong>{row.description}</p>
                                    {/*<p className="card-text"><strong>Email: </strong>{row.maintext}</p>*/}
                                </div>
                            </div>
                            <div className="card-footer d-flex justify-content-between">
                                <div className="me-auto">
                                    <Link className="btn btn-outline-primary btn-icon" to={`/content/update/${row.id}`}>
                                        <i className="bi bi-pencil"></i>
                                    </Link>
                                </div>
                                <div className="flex-grow-1"></div>
                                <div className="ms-auto">
                                    <button className="btn btn-danger btn-icon" onClick={() => deleteRow(row.id)}>
                                        <i className="bi bi-trash"></i>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

