import React, {useState, useEffect} from "react";
import {Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import '../styles/Home.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import '../styles/Cards.css'

export default function Wiki() {
    const [rows, setRows] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const location = useLocation();
    const deleteRow = async (id) => {
        confirmAlert({
            title: 'Подтвердите удаление',
            message: 'Вы действительно хотите удалить эту публикацию?',
            buttons: [
                {
                    label: 'Отмена',
                    onClick: () => {}
                },
                {
                    label: 'Удалить',
                    onClick: () => {
                        axios.delete(`http://localhost:8080/wiki/delete/${id}`).then(() => {
                            loadRows();
                        });
                    }
                }
            ]
        });
    };

    useEffect(() => {
        loadRows();
    }, []);

    const loadRows = async () => {
        const result = await axios.get("http://localhost:8080/wiki");
        setRows(result.data);
    };

    const filteredRows = rows.filter(
        (row) =>
            row.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            row.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
            row.maintext.toLowerCase().includes(searchTerm.toLowerCase())
    );

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const searchQuery = queryParams.get("search");
        setSearchTerm(searchQuery || "");
    }, [location]);

// TODO: Табличный вид

    return (
        <div className="container">
            <div className="py-4">
                <table className="table border shadow">
                    <thead>
                    <tr>
                        <th scope="col">S.N</th>
                        <th scope="col">Name</th>
                        <th scope="col">Username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {filteredRows.map((row, index) => (
                        <tr>
                            <th scope="row" key={index}>
                                {index + 1}
                            </th>
                            <td>{row.name}</td>
                            <td>{row.username}</td>
                            <td>{row.email}</td>
                            <td>
                                <Link
                                    className="btn btn-primary mx-2"
                                    to={`/wiki/${row.id}`}
                                >
                                    Посмотреть
                                </Link>
                                <Link
                                    className="btn btn-outline-primary mx-2"
                                    to={`/editrow/${row.id}`}
                                >
                                    Изменить
                                </Link>
                                <button
                                    className="btn btn-danger mx-2"
                                    onClick={() => deleteRow(row.id)}
                                >
                                    Удалить
                                </button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
