import React, {Component, useState} from "react";
import { Routes, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AuthService from "./services/auth.service";

import Login from "./components/login.component";
import Register from "./components/register.component";
import Home from "./components/home.component";
import Profile from "./components/profile.component";
import BoardModerator from "./components/board-moderator.component";
import BoardAdmin from "./components/board-admin.component";
import AddRow from "./rows/AddRow";
import EditRow from "./rows/EditRow";
import ViewRow from "./rows/ViewRow";

// import AuthVerify from "./common/auth-verify";
import EventBus from "./common/EventBus";
import About from "./pages/About";
import AboutUs from "./components/board-user.component";
import FooterComponent from "./components/footer.component";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);
    this.handleSearchChange = this.handleSearchChange.bind(this);

    this.state = {
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
      searchTerm: "",
    };
  }


  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }

    EventBus.on("logout", () => {
      this.logOut();
    });
  }

  componentWillUnmount() {
    EventBus.remove("logout");
  }

  logOut() {
    AuthService.logout();
    this.setState({
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    });
  }

  handleSearchChange(event) {
    this.setState({ searchTerm: event.target.value });
  }



  render() {
    const { currentUser, showModeratorBoard, showAdminBoard } = this.state;
    const { userRole } = this.state;
    const searchTerm = this.state.searchTerm;

    return (
      <div>
        <nav className="navbar navbar-expand navbar-dark bg-dark">
          <Link to={"/"} className="navbar-brand">
            Cr¥wo ₿oy$
          </Link>
          <div className="navbar-nav mr-auto">

            <li className="nav-item">
              {userRole === "ROLE_ADMIN" && (
                  <Link to="localhost:8081/wiki/new" className="btn btn-primary">
                    Add
                  </Link>
              )}
            </li>

            {showModeratorBoard && (
              <li className="nav-item">
                <Link to={"/mod"} className="nav-link">
                  Модер
                </Link>
              </li>
            )}

            {showAdminBoard && (
              <li className="nav-item">
                <Link to={"/admin"} className="nav-link">
                  Админ
                </Link>
              </li>
            )}

            {showAdminBoard && (
                <li className="nav-item">
                  <Link to={"wiki/new"} className="nav-link">
                    Добавить запись
                  </Link>
                </li>
            )}

            {currentUser && (
              <li className="nav-item">
                <Link to={"/about"} className="nav-link">
                  О нас
                </Link>
              </li>
            )}
          </div>

          {currentUser ? (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  {currentUser.username}
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" onClick={this.logOut}>
                  Выйти
                </a>
              </li>
            </div>
          ) : (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                {/*<Link to={"/login"} className="nav-link">*/}
                {/*  Войти*/}
                {/*</Link>*/}

                <button
                    className="btn btn-danger"
                    onClick={() => deleteRow(row.id)}
                    style={{ marginRight: "10px" }}
                >
                  <i className="bi bi-trash"></i>
                </button>
              </li>

              <li className="nav-item">
                <Link to={"/register"} className="nav-link">
                  Регистрация
                </Link>
              </li>
            </div>
          )}
        </nav>

        <div className="container mt-4">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/about" element={<AboutUs />} />
            <Route path="/mod" element={<BoardModerator />} />
            <Route path="/admin" element={<BoardAdmin />} />
            <Route exact path="/wiki/new" element={<AddRow />} />
            <Route exact path="/wiki/update/:id" element={<EditRow />} />
            <Route exact path="/wiki/:id" element={<ViewRow />} />
          </Routes>
        </div>
        <FooterComponent />
        {/* <AuthVerify logOut={this.logOut}/> */}
      </div>
    );
  }
}

export default App;
